","keccak256":"0x36a0c409c437a753cac9b92b75f93b0fbe92803bf2c8ff1517e54b247f166134","license":"MIT"},"contracts/Airdrop.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

import "@openzeppelin/contracts/utils/cryptography/MerkleProof.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "./MyMintableToken.sol";

contract Airdrop is Initializable, UUPSUpgradeable, OwnableUpgradeable {
    // ===== STATE VARIABLES =====

    MyMintableToken public token;
    bytes32 public merkleRoot;

    mapping(address => bool) public hasClaimed;

    constructor() {
        _disableInitializers();
    }

    // ===== EVENTS =====
    event MerkleRootUpdated(bytes32 oldRoot, bytes32 newRoot);
    event Claimed(address indexed account, uint256 amount);

    // ===== MODIFIERS =====
    modifier isWhitelistedAddress(bytes32[] calldata _proof) {
        require(
            verifyProof(_proof, keccak256(abi.encodePacked(msg.sender))),
            "Not Whitelisted Address"
        );
        _;
    }

    modifier isClaimed(address _account ) {
        require(
            !hasClaimed[_account],
            "Already Claimed !!!"
        );
        _;
    }

    // ===== INITIALIZER =====

    function initialize(address _tokenAddress, bytes32 _merkleRoot) public initializer {
        __Ownable_init(msg.sender);
        __UUPSUpgradeable_init();
        token = MyMintableToken(_tokenAddress);
        merkleRoot = _merkleRoot;
    }

    // ===== VERIFICATION FUNCTIONS =====

    function verifyProof(bytes32[] calldata _proof, bytes32 _leaf) private view returns(bool) {
        return MerkleProof.verify(_proof, merkleRoot, _leaf);
    }

    // ===== ADMIN FUNCTIONS =====

    function setMerkleRoot(bytes32 _newRoot) external onlyOwner {
        bytes32 _oldRoot = merkleRoot;
        merkleRoot = _newRoot;
        emit MerkleRootUpdated(_oldRoot, _newRoot);
    }

    // ===== CLAIM FUNCTIONS =====
    function claim(
        uint256 _amount,
        bytes32[] calldata _proof
    ) external isWhitelistedAddress(_proof) isClaimed(msg.sender) {
        require(_amount > 0, "Invalid Amount");
        require(_proof.length > 0, "Invalid Proof");

        hasClaimed[msg.sender] = true;

        // Mint token for this 'msg.sender'
        token.ownerMint(msg.sender, _amount);

        // Emitting Claimed
        emit Claimed(msg.sender, _amount);
    }

    function _authorizeUpgrade(address) internal override onlyOwner {}

    // Create 50 empty storage slots for future upgrade
    uint256[50] private __gap;
}","keccak256":"0x15f46aa014111b2dd5e6ccd3b0e8dc7965eff071b580e049b3d18d7262bf2b65","license":"MIT"},"contracts/MyMintableToken.sol":{"content":"// SPDX-License-Identifier: MIT 
pragma solidity ^0.8.28;

// Import ERC20 and Ownable
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract MyMintableToken is ERC20, Ownable {

    // ====== CONSTRUCTOR ======

    constructor() ERC20("MyMintableToken", "MMT") Ownable(msg.sender) {}

    // Mint token
    function ownerMint(address _to, uint256 _amount) public onlyOwner {
        _mint(_to, _amount);
    }

    // Burn token
    function burn(uint256 _amount) public {
        _burn(msg.sender, _amount);
    }
}","keccak256":"0x279426542a888fdd4c481ec567ada628518da97fe186b3f5a6b8c193769bbc20","license":"MIT"}},"version":1}
